t.script:
function subst() {
    var vars = {};
    var x = document.location.search.substring(1).split('&');
    for (var i in x) {
        var z = x[i].split('=', 2);
        vars[z[0]] = unescape(z[1]);
    }
    var x=['frompage', 'topage', 'page', 'webpage', 'section', 'subsection', 'subsubsection'];
    for (var i in x) {
        var y = document.getElementsByClassName(x[i]);
        for (var j=0; j<y.length; ++j)
            y[j].textContent = vars[x[i]];
    }
    var operations =
    {
        'not-first-page': function (elt) {
            elt.style.visibility = (vars.page === vars.frompage) ? "hidden" : "visible";
        },
        'not-last-page': function (elt) {
            elt.style.visibility = (vars.page === vars.topage) ? "hidden" : "visible";
        },
        'first-page': function (elt) {
            elt.style.visibility = (vars.page === vars.frompage) ? "visible" : "hidden";
        },
        'last-page': function (elt) {
            elt.style.visibility = (vars.page === vars.topage) ? "visible" : "hidden";
        },
        'odd-page': function (elt) {
            elt.style.display = (vars.page % 2 != 0) ? elt.style.display : "none";
        },
        'not-odd-page': function (elt) {
            elt.style.display = (vars.page % 2 != 0) ? elt.style.display : "none";
        },
        'hidden-if-one-page': function (elt) {
            elt.style.visibility = (vars.topage > 1) ? "visible" : "hidden";
        },
        'hidden-even-page': function (elt) {
            elt.style.visibility = (vars.page % 2 == 1) ? "visible" : "hidden";
        },
        'hidden-odd-page': function (elt) {
            elt.style.visibility = (vars.page % 2 == 0) ? "visible" : "hidden";
        },
    };
    for (var klass in operations) {
        var y = document.getElementsByClassName(klass);
        for (var j=0; j<y.length; ++j)
            operations[klass](y[j]);
    }
}
